mapper.update:
UPDATE comment_tb
SET content =  #{content}
Where    username = #{username}
  AND _id      = #{_id}

UPDATE community_tb
SET commentcount = commentcount + 1
where  _id = #{content_id}
  and    menu_id=#{menu_id}

UPDATE community_tb
SET commentcount = commentcount - 1
where  _id = #{content_id}
  and  menu_id=#{menu_id}
