mapper.update:
UPDATE content_tb
SET    STEP = STEP + 1
WHERE  bnum = #{ bnum }
AND    STEP > #{ step }

mapper.select:
SELECT _id, username, title, content, category, time, readcount, bnum, lvl, step
FROM content_tb

    WHERE
    category = #{category}

ORDER BY bnum desc, step  ASC
LIMIT #{displaypost},#{postnum}

SELECT COUNT(_id) FROM content_tb

    WHERE
    category = #{category}

SELECT _id, username, title, content, category, time, readcount
FROM content_tb
WHERE _id = #{_id}

SELECT _id, username, title, content, category, time, readcount, bnum, lvl, step
FROM content_tb
WHERE _id = #{_id}

SELECT filename, filepath, content_id
FROM   content_files_tb
WHERE  content_id = #{_id}

mapper.update:
UPDATE content_tb
SET    readcount = readcount + 1
WHERE  _id  = #{_id}

UPDATE content_tb
SET    title = #{title},
       content = #{content}
WHERE  _id = #{_id}
